import React, { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Scatter } from 'recharts';

const TechnicalIndicatorChart = ({ data, indicator, color }) => (
  <ResponsiveContainer width="100%" height={300}>
    <LineChart data={data}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="date" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey={indicator} stroke={color} dot={false} />
    </LineChart>
  </ResponsiveContainer>
);

const PriceMovementChart = ({ data, signals }) => (
  <ResponsiveContainer width="100%" height={400}>
    <LineChart data={data}>
      <CartesianGrid strokeDasharray="3 3" />
      <XAxis dataKey="date" />
      <YAxis />
      <Tooltip />
      <Legend />
      <Line type="monotone" dataKey="close" stroke="#8884d8" dot={false} />
      {Object.entries(signals).map(([indicator, signalData]) => (
        <Scatter
          key={indicator}
          name={`${indicator} Signal`}
          data={signalData}
          fill={getIndicatorColor(indicator)}
          shape="star"
        />
      ))}
    </LineChart>
  </ResponsiveContainer>
);

const IndicatorTable = ({ data }) => (
  <table className="min-w-full bg-white">
    <thead>
      <tr>
        <th className="px-4 py-2 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
          Date
        </th>
        <th className="px-4 py-2 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
          Indicator
        </th>
        <th className="px-4 py-2 border-b-2 border-gray-200 bg-gray-100 text-left text-xs font-semibold text-gray-600 uppercase tracking-wider">
          Value
        </th>
      </tr>
    </thead>
    <tbody>
      {data.map((row, index) => (
        <tr key={index}>
          <td className="px-4 py-2 border-b border-gray-200">{row.date}</td>
          <td className="px-4 py-2 border-b border-gray-200">{row.indicator}</td>
          <td className="px-4 py-2 border-b border-gray-200">{row.value.toFixed(2)}</td>
        </tr>
      ))}
    </tbody>
  </table>
);

const getIndicatorColor = (indicator) => {
  const colors = {
    SMA: '#FF6384',
    RSI: '#36A2EB',
    MACD: '#FFCE56',
    BB: '#4BC0C0',
    Stoch: '#9966FF',
    Ichimoku: '#FF9F40',
    CCI: '#FF6384',
  };
  return colors[indicator] || '#000000';
};

const Dashboard = () => {
  const [data, setData] = useState([]);
  const [indicatorData, setIndicatorData] = useState([]);
  const [signals, setSignals] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/sugar-options-data');
        const jsonData = await response.json();
        setData(jsonData.data);
        setIndicatorData(jsonData.indicatorData);
        setSignals(jsonData.signals);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">Sugar Options Technical Analysis Dashboard</h1>

      <h2 className="text-2xl font-semibold mt-8 mb-4">Price Movement with Indicator Signals</h2>
      <PriceMovementChart data={data} signals={signals} />

      <h2 className="text-2xl font-semibold mt-8 mb-4">Technical Indicators</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <TechnicalIndicatorChart data={data} indicator="SMA_20" color="#FF6384" />
        <TechnicalIndicatorChart data={data} indicator="RSI" color="#36A2EB" />
        <TechnicalIndicatorChart data={data} indicator="MACD_12_26_9" color="#FFCE56" />
        <TechnicalIndicatorChart data={data} indicator="BBM_20_2.0" color="#4BC0C0" />
        <TechnicalIndicatorChart data={data} indicator="STOCHk_14_3_3" color="#9966FF" />
        <TechnicalIndicatorChart data={data} indicator="ISA_9" color="#FF9F40" />
        <TechnicalIndicatorChart data={data} indicator="CCI" color="#FF6384" />
      </div>

      <h2 className="text-2xl font-semibold mt-8 mb-4">Indicator Signals Table</h2>
      <IndicatorTable data={indicatorData} />
    </div>
  );
};

export default Dashboard;
